function GitHub(){this.data={},this.commits=[],this.user="",this.repos=[]}GitHub.prototype.getCommits=function(t,o){var s=this,i=this.user;jsonp("https://api.github.com/repos/"+i+"/"+t+"/commits?per_page=1000000&callback=json_callback",function(t){return t.data.forEach(function(t){s.commits.push(t.commit)}),o()})},GitHub.prototype.getRepos=function(t){var o=this;jsonp("https://api.github.com/users/"+this.user+"/repos?callback=json_callback",function(s){var i=[];return s.data.forEach(function(t){i.push(t.name)}),o.repos=i,t()})},GitHub.prototype.getAllCommits=function(){var t=this;this.getRepos(function(){var o=t.repos.length;t.repos.forEach(function(s){t.getCommits(s,function(){o--,0==o&&(alert("done"),console.log(t.commits))})})})};